import { useEffect } from "react";
import { Collapse } from "antd";
import { useForm,Controller} from "react-hook-form";
import { useDispatch, useSelector } from "react-redux";
import { getUserDataByBirthDay,getBlockedUserData, updateUserBlocking } from "../../Redux/Features/userReqests/getUserData";
import UserItem from "../../Camponents/userItems/UserItem";
import BlockedUserItem from '../../Camponents/userItems/BlockedUserItem'


function GetUsersData(){
    const dispatch = useDispatch();
    const userData = useSelector((state)=>state.getUsersDataByBirthday.userDataState);
    const blockedUsers = useSelector((state)=>state.getUsersDataByBirthday.blockedUserDataState);

    const {
        register,
        handleSubmit,
        con
    }=useForm();

    const submit = (date)=>{
        dispatch(getUserDataByBirthDay(date.birthday));
        
    };
    const getBlocked = ()=>{
        dispatch(getBlockedUserData())
      
    };
    const submitBlocking = (data)=>{
        dispatch(updateUserBlocking(data))
    }
   
    const userLabels = userData&&userData.map((item)=>({
        label:<span style={{ color: 'white' }}>{item.userName}</span>,
        key:item.id,
        children:<UserItem 
        userName={item.userName}
        email={item.email}
        birthDate={item.birthDate}
        phoneNumber={item.phoneNumber.phoneNumber}
        id={item.id}
        apartmentNumber={item.address.apartmentNumber}
        city={item.address.city}
        houseNumber={item.address.houseNumber}
        streetName={item.address.streetName}

        ></UserItem>
    }))
    const blockedUserLabels = blockedUsers&&blockedUsers.map((item)=>({
        label:<span style={{ color: 'white' }}>{item.userName}</span>,
        key:item.id,
        children:<BlockedUserItem 
        userName={item.userName}
        id={item.id}
        reviewDate={item.reviewDate}
        blocked = {item.blocked}
        ></BlockedUserItem>
    }))
    return(<div>
        <form onSubmit={handleSubmit(submit)}>
            <label htmlFor="birthday">Birth Date</label>
            <input type="date" {...register('birthday')}/>
            <input type="submit" value='get info' />
        </form>
        <form>
            <label htmlFor="userId">User ID</label>
            <input type="number"{...register('userId')}/>
            <label htmlFor="userStatus">User Status</label>
            <select defaultValue='' onChange={handleChange}>
            <option value='true'>Blocked</option>
            <option value='false'>Active</option>
          </select>
          <input type="submit" />
        </form>
        <button onClick={getBlocked}>Get Blocked Users</button>
        {userLabels&&<Collapse accordion items={userLabels}/>}
        {blockedUserLabels&&<Collapse accordion items={blockedUserLabels}/>}
    </div>)
}
export default GetUsersData;
