import {  useEffect } from "react";
import { Collapse } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { getStandartPizza } from "../../Redux/Features/PizzaReqest/pizzaReqestSlice";
import StandartPizzaItem from "../../Camponents/PizzaItems/StandartPizzaItem";
import UpdatePizzaForm from "../../Camponents/PizzaForms/UpdatePizzaForm";
import listStyles from './pizzalistPage.module.css'

function PizzaListPage() {
  const dispatch = useDispatch();

  const pizzaList = useSelector(
    (state) => state.pizzaReqest.allStandartPizzaDataState
  );
  useEffect(() => {
    dispatch(getStandartPizza());
  }, [dispatch]);
  const pizzaLabels =pizzaList&&pizzaList.map((item)=>({
     label:<span style={{ color: 'white' }}>{item.title}</span>,
      key:item.id,
      children:<StandartPizzaItem
            amount={item.amount}
            ingredientsList={item.ingredientsList}
            nutrition={item.nutrition}
            size={item.size}
            styles={item.styles}
            title={item.title}
            toppingsFillings={item.toppingsFillings}
          ></StandartPizzaItem>
  }
  ))
  const udatePizzaLabels = pizzaList&&pizzaList.map((item)=>({
    label:<span style={{ color: 'white' }}>{item.title}</span>,
    key:item.id,
    children:<UpdatePizzaForm
    id={item.id}
    
    ></UpdatePizzaForm>
  }))
  return (
    <div className={listStyles.listContainer}>
    <div>
        <Collapse accordion items={pizzaLabels}/>
    </div>
    </div>
  );
}
export default PizzaListPage;
