import {useForm} from "react-hook-form";

function RegistrationForm(){
    const{
        register,
        handleSubmit,
        watch,
        formState:{errors},
    }=useForm();
    const loginRegEx = /^[a-zA-Z0-9_]{5,25}$/;
    const passwordRegex = /^[a-zA-Z0-9_]{5,15}$/;
    const emailRegex =/^[\w-]+@([\w-]+\.)+[\w-]{2,4}$/g;

    return(
        <form onSubmit={handleSubmit((data)=>console.log(data))}>
        <label htmlFor="login">Login</label>
        <input  placeholder="Login"{...register('login',{required:"Login is reqired!",
        pattern:{
            value:loginRegEx,
            message:"loggin mut contain aleast 5 symbols and use only letters and numbers"
        }})}></input>
        {errors.login&&errors.login.type==="required"&&<span>{errors.login.message}</span>}
        {errors.login&&errors.login.type==="pattern"&&<span>{errors.login.message}</span>}
        <label htmlFor="password">Password</label>
        <input  placeholder="Password"{...register('password',{required:"Password is reqired!",
        pattern:{
            value:passwordRegex,
            message:"Password mut contain aleast 5 symbols and use only letters and numbers"
        }})}></input>
        {errors.password&&errors.password.type==="required"&&<span>{errors.password.message}</span>}
        {errors.password&&errors.password.type==="pattern"&&<span>{errors.password.message}</span>}
        <label htmlFor="email">Email</label>
        <input  placeholder="Email"{...register('email',{required:"Email is reqired!",
        pattern:{
            value:emailRegex,
            message:"Incorect input type"
        }})}></input>
        {errors.email&&errors.email.type==="required"&&<span>{errors.email.message}</span>}
        {errors.email&&errors.email.type==="pattern"&&<span>{errors.email.message}</span>}
        <label htmlFor="birthDate">Birth Date</label>
        <input type="date"{...register('birthDate')} />
        <label htmlFor="email">Email</label>
        <input  placeholder="Email"{...register('email',{required:"Email is reqired!",
        pattern:{
            value:emailRegex,
            message:"Incorect input type"
        }})}></input>
        {errors.email&&errors.email.type==="required"&&<span>{errors.email.message}</span>}
        {errors.email&&errors.email.type==="pattern"&&<span>{errors.email.message}</span>}

        <input type="submit" />
        </form>
    );
};
export default RegistrationForm;
