import { useForm } from "react-hook-form";
import { useDispatch,useSelector} from "react-redux";
import { getIngredients } from "../../Redux/Features/AdminReqests/ingredientReqestsSlice";
import IngredientItem from "../IngredientItem/IngredientItem";

function IngredientListPage() {
  
    const {
      register,
      handleSubmit,
      formState: { errors },
    } = useForm();
    const dispatch = useDispatch();
    const ingredientList = useSelector((state)=>state.ingredientReqest.ingredientListDataState)
    const submit = (data)=>{
        dispatch(getIngredients(data.groupIngredient));
        
    }

    return (
        <div>
            <form onSubmit={handleSubmit(submit)}>
            <label htmlFor="groupIngredient">Dough</label>
        <select id="groupIngredient" {...register("groupIngredient")}>
          <option value="SAUCE">SAUCE</option>
          <option value="BASIC">BASIC</option>
          <option value=" EXTRA"> EXTRA</option>
        </select>
        <input type="submit" value='Get list' />
            </form>
            {ingredientList&&ingredientList.map((item)=>{
              return(<>
                <IngredientItem key={item.id} name={item.name}weight={item.weight}nutrition={item.nutrition}price={item.price}groupIngredient={item}></IngredientItem>
                </>
              )
            })}
        </div>
    );
  }

export default IngredientListPage;
