import { useEffect } from "react";
import Select from 'react-select';
import { useDispatch,useSelector } from "react-redux";
import { getSauce } from "../../Redux/Features/AdminReqests/ingredientReqestsSlice";
import {Controller, useForm} from 'react-hook-form';
import { useState } from "react";


function PizzaForm(){
    const{
        register,
        handleSubmit,
        formState:{errors},
        control,
    }= useForm();
    const [sauceOptions,setSauceOptions] = useState([]);
    const dispatch = useDispatch()
    const sauceList = useSelector((state)=>state.ingredientReqest.sauceDataState);
    useEffect(()=>{
       dispatch(getSauce());
       
        
    },[])
    const submit = (data)=>{
        console.log(data)
    }
    const handleChange = (selected) => {
        if (selected.length <= 3) {
         setSauceOptions(selected);
        }
      };

    return(<div>
        <form onSubmit={handleSubmit(submit)}>
        <Controller
        control={control}
        name="SAUCE"
        render={({ field }) => (
          <Select
            {...field}
            options={sauceList&&sauceList.map((sauce)=>({
                value:sauce.id,
                label:sauce.name
            }))} 
            value={sauceOptions}
            isMulti
            onChange={handleChange}
            placeholder='Choise a sauce'
          />
        )}
      />
        </form>
    </div>);
}
export default PizzaForm;
