import { useEffect } from 'react';
import {Select} from 'antd'
import { toppingFillingsArr,stylesArr } from '../../utils';
import {useDispatch,useSelector} from 'react-redux'
import { getPizzaByTopping } from '../../Redux/Features/PizzaReqest/pizzaReqestSlice';

function PizzaGetFilter(){
    const dispatch = useDispatch();
    const {Option,OptGroup}=Select;
    const pizzaList = useSelector((state)=>state.pizzaReqest.allStandartPizzaDataState);

    const handleToppingChange = (value)=>{
        dispatch(getPizzaByTopping(value));
    };
    console.log(pizzaList)
    return(
    <div>
        <Select defaultValue='Get by topping' onChange={handleToppiChange}>
        {toppingFillingsArr&&toppingFillingsArr.map((item)=>(
            <Option value={item.value}>{item.value}</Option>
        ))}
        </Select>
    </div>
    )
}
export default PizzaGetFilter;
