import {useForm} from "react-hook-form";

function RegistrationForm(){
    const{
        register,
        handleSubmit,
        watch,
        formState:{errors},
    }=useForm();
    const loginRegEx = /^[a-zA-Z0-9_]{5,25}$/;

    return(
        <form onSubmit={handleSubmit((data)=>console.log(data))}>
            
        <input  placeholder="Login"{...register('login',{required:"Login is reqired!",
        pattern:{
            value:loginRegEx,
            message:"loggin mut contain aleast 5 symbols and use only letters and numbers"
        }})}></input>
        {errors.login&&errors.login.type==="required"&&<span>{errors.login.message}</span>}
        {errors.login&&errors.login.type==="pattern"&&<span>{errors.login.message}</span>}
        <input type="submit" />
        </form>
    );
};
export default RegistrationForm;
