    <div>
      <form onSubmit={handleSubmit(submit)}>
        <Controller
          control={control}
          name="ingredientsSauceListId"
          render={({ field }) => (
            <Select
              {...field}
              options={
                sauceList &&
                sauceList.map((sauce) => ({
                  value: sauce.id,
                  label: sauce.name,
                }))
              }
              value={sauceOptions}
              isMulti
              onChange={(selected) => {
                field.onChange(handleChange(selected));
              }}
              placeholder="Choose a sauce"
            />
          )}
        />
        <input type="submit" />
      </form>
    </div>
  );
}
export default PizzaForm;
