import { createSlice } from "@reduxjs/toolkit";
import { createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

export const userRegReqest = createAsyncThunk(
    'userReg/userregReqest',
    async(data)=>{
        try{
            const reponse = await axios.post('https://jsonplaceholder.typicode.com/posts',data)
        }catch(error){
            throw error;
        }
    }
)

const userRegSlice = createSlice({
    name:'userReg',
    initialState:{
        userregReqest: null,
        loading:false,
        error:null,
    },
    reducers:{},
    extraReducers:{
        [userRegReqest.pending]:(state)=>{
            state.loading = true;
            state.error = null;
        },
        [userRegReqest.fulfilled]:(state,action)=>{
            state.loading = false;
            state.userRegReqest = action.payload;
        },
        [userRegReqest.rejected]:(state,action)=>{
            state.loading = false;
            state.error = action.error.message;
        }
    }
})
 export default userRegSlice.reducer;
