import {createSlice} from '@reduxjs/toolkit';
import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const addIngredient = createAsyncThunk(
    'addIngredient/addIngredient',
    async (data)=>{
        try{
            const response = await axios.post('http://192.168.1.102:8090/product/addIngredient',data)
            return response.data;
            
        }catch(error){
            throw error;
        }
    }
)
export const getIngredients = createAsyncThunk(
    'getIngredient/getIngredient',
    async (data)=>{
        try{
            const response = await axios.get(`http://192.168.1.102:8090/product/getAllIngredientByGroup?groupIngredient=${data}`)
            console.log(response.data)
            return response.data;
            
        }catch(error){
            throw error;
        }
    }
)
export const updateDough = createAsyncThunk(
    'updateDough/updateDough',
    async ({id,data}) => {
       
        const {smallNutrition,smallWeight}=data
        try {
           
            const response = await axios.patch(`http://192.168.1.102:8090/product/updateDough/${id}`,{...data,
            smallWeight: parseInt(smallWeight),
            smallNutrition: parseInt(smallNutrition)
        });
            return response.data;
        } catch (error) {
            throw error;
        }
    }
);

export const doughDelete = createAsyncThunk(
    'doughDelete/doughDelete',
    async (id)=>{
        try{      
            const response = await axios.delete(`http://192.168.1.102:8090/product/deleteDough/${id}`)
            return response.data;
        }catch(error){
            throw error;
        }
    }
)


const   ingredientreqestSlice = createSlice({
    name:'ingredientReqest',
    initialState:{
        ingredientDataState:null,
        ingredientLoadState:false,
        ingredientErrorState:null,
        
    },
    reducers:{},
    extraReducers:(builder)=>{
        builder
        .addCase(addIngredient.pending,(state)=>{
            state.ingredientLoadState = true;
            state.ingredientErrorState = null;
        })
        .addCase(addIngredient.fulfilled,(state,action)=>{
            state.ingredientDataState = action.payload;
            state.ingredientLoadState = false;
            state.ingredientErrorState = null;
        })
        .addCase(addIngredient.rejected,(state,action)=>{
            state.ingredientLoadState = false;
            state.ingredientErrorState = action.payload;
        })
        .addCase(getIngredients.pending,(state)=>{
            state.ingredientLoadState = true;
            state.ingredientErrorState = null;
        })
        .addCase(getIngredients.fulfilled,(state,action)=>{
            state.ingredientDataState = action.payload;
            state.ingredientLoadState = false;
            state.ingredientErrorState = null;
        })
        .addCase(getIngredients.rejected,(state,action)=>{
            state.ingredientLoadState = false;
            state.ingredientErrorState = action.payload;
        })

    }
})
export default ingredientreqestSlice.reducer;
